import React, { Component } from 'react';
import GalaxiesService from '../services/GalaxiesService';
import backgroundImage from '../images/background.jpg';
import octocatImage from '../images/octocat.png';
import './galaxies.css';

const AVAILABLE_GALAXY_IMAGES = [backgroundImage, octocatImage];
const DEFAULT_GALAXY_IMAGE = AVAILABLE_GALAXY_IMAGES[0] || 'https://placehold.co/160x160?text=Galaxy';

export default class Galaxies extends Component {
    state = {
        galaxies: [],
        showCreateModal: false,
        isSubmitting: false,
        formError: null,
        newGalaxy: {
            name: '',
            description: '',
            status: 'open',
        },
    };

    constructor(props) {
        super(props);
        this.onClickViewGalaxy = this.onClickViewGalaxy.bind(this);
        this.onClickEditGalaxy = this.onClickEditGalaxy.bind(this);
        this.onClickDeleteGalaxy = this.onClickDeleteGalaxy.bind(this);
        this.resolveStatusVariant = this.resolveStatusVariant.bind(this);
        this.openCreateModal = this.openCreateModal.bind(this);
        this.closeCreateModal = this.closeCreateModal.bind(this);
        this.handleFormChange = this.handleFormChange.bind(this);
        this.handleCreateGalaxy = this.handleCreateGalaxy.bind(this);
        this.randomGalaxyImage = this.randomGalaxyImage.bind(this);
    }

    componentDidMount() {
        console.log("Fetching galaxies from the API");
        console.log("API URL:", process.env.REACT_APP_GALAXIES_API_URL);
        GalaxiesService.getGalaxies()
            .then(galaxies => this.setState({ galaxies }))
            .catch(error => console.error('Error fetching galaxies:', error));
    }

    onClickViewGalaxy(galaxyId) {
        console.log("View galaxy", galaxyId);
        this.props.history.push(`/galaxies/${galaxyId}`);
    }

    onClickEditGalaxy(galaxyId, event) {
        event.stopPropagation();
        console.log("Edit galaxy", galaxyId);
    }

    onClickDeleteGalaxy(galaxyId, event) {
        event.stopPropagation();
        console.log("Delete galaxy", galaxyId);
    }

    openCreateModal() {
        this.setState({
            showCreateModal: true,
            formError: null,
            newGalaxy: {
                name: '',
                description: '',
                status: 'open',
            },
        });
    }

    closeCreateModal() {
        if (this.state.isSubmitting) {
            return;
        }
        this.setState({ showCreateModal: false, formError: null });
    }

    handleFormChange(event) {
        const { name, value } = event.target;
        this.setState((prevState) => ({
            newGalaxy: {
                ...prevState.newGalaxy,
                [name]: value,
            },
        }));
    }

    randomGalaxyImage() {
        if (!AVAILABLE_GALAXY_IMAGES.length) {
            return DEFAULT_GALAXY_IMAGE;
        }
        const index = Math.floor(Math.random() * AVAILABLE_GALAXY_IMAGES.length);
        return AVAILABLE_GALAXY_IMAGES[index];
    }

    handleCreateGalaxy(event) {
        event.preventDefault();
        const { newGalaxy } = this.state;
        if (!newGalaxy.name.trim()) {
            this.setState({ formError: 'Name is required.' });
            return;
        }
        const payload = {
            ...newGalaxy,
            image: this.randomGalaxyImage(),
        };

        this.setState({ isSubmitting: true, formError: null });
        GalaxiesService.createGalaxy(payload)
            .then((createdGalaxy) => {
                const hydratedGalaxy = createdGalaxy && createdGalaxy.id
                    ? { ...createdGalaxy, image: createdGalaxy.image || payload.image }
                    : { ...payload, id: Date.now() }; // fallback for mock API

                this.setState((prevState) => ({
                    galaxies: [...prevState.galaxies, hydratedGalaxy],
                    showCreateModal: false,
                    isSubmitting: false,
                    newGalaxy: {
                        name: '',
                        description: '',
                        status: 'open',
                    },
                }));
            })
            .catch((err) => {
                const errorMessage = err && err.message ? err.message : 'Failed to create galaxy.';
                this.setState({
                    formError: errorMessage,
                    isSubmitting: false,
                });
            });
    }

    resolveStatusVariant(status) {
        switch ((status || '').toLowerCase()) {
            case 'completed':
                return 'bg-success';
            case 'open':
                return 'bg-info';
            case 'pending':
                return 'bg-warning text-dark';
            default:
                return 'bg-secondary';
        }
    }

    render() {
        const { galaxies, showCreateModal, isSubmitting, newGalaxy, formError } = this.state;
        return (
            <div className="full-height-container">
                <div className="text-center text-white py-5">
                    <h1>Galaxies</h1>
                    <p>These are all the galaxies listed in the database</p>
                    <button className="btn btn-primary create-galaxy-btn" onClick={this.openCreateModal}>
                        Create Galaxy
                    </button>
                    <div className="container">
                        <div className="accordion galaxy-accordion" id="galaxiesAccordion">
                            {galaxies.length === 0 && (
                                <div className="empty-state text-muted">
                                    No galaxies available yet.
                                </div>
                            )}
                            {galaxies.map((galaxy) => (
                                <div className="accordion-item galaxy-item" key={galaxy.id}>
                                    <h2 className="accordion-header" id={`heading-${galaxy.id}`}>
                                        <button
                                            className="accordion-button collapsed"
                                            type="button"
                                            data-bs-toggle="collapse"
                                            data-bs-target={`#collapse-${galaxy.id}`}
                                            aria-expanded="false"
                                            aria-controls={`collapse-${galaxy.id}`}
                                        >
                                            <div className="galaxy-summary">
                                                <img
                                                    src={galaxy.image || DEFAULT_GALAXY_IMAGE}
                                                    className="galaxy-thumb"
                                                    alt={`${galaxy.name || 'Unknown galaxy'} illustration`}
                                                />
                                                <div className="galaxy-meta">
                                                    <span className="galaxy-name">{galaxy.name}</span>
                                                    <span className={`badge galaxy-status ${this.resolveStatusVariant(galaxy.status)}`}>
                                                        {galaxy.status || 'Unknown'}
                                                    </span>
                                                </div>
                                            </div>
                                        </button>
                                    </h2>
                                    <div
                                        id={`collapse-${galaxy.id}`}
                                        className="accordion-collapse collapse"
                                        aria-labelledby={`heading-${galaxy.id}`}
                                        data-bs-parent="#galaxiesAccordion"
                                    >
                                        <div className="accordion-body galaxy-body">
                                            <p className="galaxy-description">{galaxy.description || 'No description provided.'}</p>
                                            <div className="galaxy-actions">
                                                <button
                                                    className="btn btn-outline-primary"
                                                    onClick={(event) => {
                                                        event.stopPropagation();
                                                        this.onClickViewGalaxy(galaxy.id);
                                                    }}
                                                >
                                                    View details
                                                </button>
                                                <button
                                                    className="btn btn-outline-secondary"
                                                    onClick={(event) => this.onClickEditGalaxy(galaxy.id, event)}
                                                >
                                                    Edit
                                                </button>
                                                <button
                                                    className="btn btn-outline-danger"
                                                    onClick={(event) => this.onClickDeleteGalaxy(galaxy.id, event)}
                                                >
                                                    Delete
                                                </button>
                                            </div>
                                        </div>
                                    </div>
                                </div>
                            ))}
                        </div>
                    </div>
                </div>
                {showCreateModal && (
                    <div className="modal-backdrop-custom" role="dialog" aria-modal="true">
                        <div className="modal-dialog-custom" role="document">
                            <div className="modal-content-custom">
                                <div className="modal-header-custom">
                                    <h5 className="modal-title">Create New Galaxy</h5>
                                    <button
                                        type="button"
                                        className="btn-close"
                                        onClick={this.closeCreateModal}
                                        aria-label="Close"
                                    />
                                </div>
                                <form onSubmit={this.handleCreateGalaxy}>
                                    <div className="modal-body-custom">
                                        <div className="mb-3 text-start">
                                            <label className="form-label" htmlFor="galaxy-name">Name</label>
                                            <input
                                                id="galaxy-name"
                                                name="name"
                                                type="text"
                                                className="form-control"
                                                value={newGalaxy.name}
                                                onChange={this.handleFormChange}
                                                placeholder="Enter galaxy name"
                                                required
                                            />
                                        </div>
                                        <div className="mb-3 text-start">
                                            <label className="form-label" htmlFor="galaxy-description">Description</label>
                                            <textarea
                                                id="galaxy-description"
                                                name="description"
                                                className="form-control"
                                                value={newGalaxy.description}
                                                onChange={this.handleFormChange}
                                                placeholder="Describe this galaxy"
                                                rows="3"
                                            />
                                        </div>
                                        <div className="mb-3 text-start">
                                            <label className="form-label" htmlFor="galaxy-status">Status</label>
                                            <select
                                                id="galaxy-status"
                                                name="status"
                                                className="form-select"
                                                value={newGalaxy.status}
                                                onChange={this.handleFormChange}
                                            >
                                                <option value="open">Open</option>
                                                <option value="completed">Completed</option>
                                                <option value="pending">Pending</option>
                                            </select>
                                        </div>
                                        <div className="mb-1 text-start">
                                            <small className="text-muted">
                                                An image will be selected automatically from the galaxy gallery.
                                            </small>
                                        </div>
                                        {formError && (
                                            <div className="alert alert-danger mt-3" role="alert">
                                                {formError}
                                            </div>
                                        )}
                                    </div>
                                    <div className="modal-footer-custom">
                                        <button
                                            type="button"
                                            className="btn btn-outline-secondary"
                                            onClick={this.closeCreateModal}
                                            disabled={isSubmitting}
                                        >
                                            Cancel
                                        </button>
                                        <button
                                            type="submit"
                                            className="btn btn-primary"
                                            disabled={isSubmitting}
                                        >
                                            {isSubmitting ? 'Creating…' : 'Create Galaxy'}
                                        </button>
                                    </div>
                                </form>
                            </div>
                        </div>
                    </div>
                )}
            </div>
        );
    }
}
